// Input: {content, visibility: 'PUBLIC'|'CONNECTIONS'}
// Output: Formatted for LinkedIn

const content = $json.content || $json.text || $json.hook || '';
const visibility = $json.visibility || 'PUBLIC';

// LinkedIn has a 3000 character limit for posts
const maxLength = 3000;
let postText = content;

if (postText.length > maxLength) {
  postText = postText.substring(0, maxLength - 3) + '...';
}

return [{
  json: {
    postText,
    visibility,
    originalLength: content.length,
    truncated: content.length > maxLength
  }
}];

const response = $json;
const input = $items('Format for LinkedIn')[0]?.json || {};

return [{
  json: {
    success: !!response.id,
    postId: response.id,
    postText: input.postText,
    timestamp: new Date().toISOString(),
    errors: response.error || null
  }
}];
