// Input: {content, visibility: 'public'|'unlisted'|'private'|'direct'}
// Output: Formatted for Mastodon

const content = $json.content || $json.text || $json.hook || '';
const visibility = $json.visibility || 'public';

// Mastodon has a 500 character limit
const maxLength = 500;
let statusText = content;

if (statusText.length > maxLength) {
  statusText = statusText.substring(0, maxLength - 3) + '...';
}

return [{
  json: {
    statusText,
    visibility,
    originalLength: content.length,
    truncated: content.length > maxLength
  }
}];

const response = $json;
const input = $items('Format for Mastodon')[0]?.json || {};

return [{
  json: {
    success: !!response.id,
    statusId: response.id,
    statusText: input.statusText,
    url: response.url || null,
    timestamp: new Date().toISOString(),
    errors: response.error || null
  }
}];
