// Input: {content, media_url (optional)}
// Output: Formatted for Twitter

const content = $json.content || $json.hook || $json.text || '';
const mediaUrl = $json.media_url || $json.mediaUrl || '';

// Twitter character limit
const maxLength = 280;
let tweetText = content;

// Truncate if needed
if (tweetText.length > maxLength) {
  tweetText = tweetText.substring(0, maxLength - 3) + '...';
}

return [{
  json: {
    tweetText,
    mediaUrl,
    originalLength: content.length,
    truncated: content.length > maxLength
  }
}];

// Format response
const response = $json;
const input = $items('Format for Twitter')[0]?.json || {};

return [{
  json: {
    success: !response.errors,
    tweetId: response.data?.id,
    tweetText: input.tweetText,
    url: response.data?.id ? `https://twitter.com/i/web/status/${response.data.id}` : null,
    timestamp: new Date().toISOString(),
    errors: response.errors || null
  }
}];
