// Input: {recommendations: [{time, channel, score, reason}], user_id, content}
const body = $json.body || $json;
const recommendations = body.recommendations || [];
const user_id = body.user_id || 'demo-user';
const content = body.content || body.ideas || [];

// Validate recommendations
if (recommendations.length === 0) {
  return [{json: {error: 'No recommendations provided', user_id}}];
}

return [{json: {user_id, content, recommendations, reminder_count: recommendations.length}}];

// Split recommendations into individual reminder jobs
const recommendations = $json.recommendations || [];
const base = $json;

return recommendations.map(rec => ({
  json: {
    ...base,
    reminder: rec,
    scheduled_time: rec.time,
    channel: rec.channel,
    score: rec.score,
    reason: rec.reason
  }
}));

// Store reminder in Supabase for tracking
const reminder = $items('Split into Reminder Jobs')[0]?.json?.reminder || {};
const user_id = $items('Split into Reminder Jobs')[0]?.json?.user_id || '';
const scheduled = $json;

const reminderRecord = {
  user_id,
  reminder_id: scheduled.jobId || `reminder-${Date.now()}`,
  scheduled_time: reminder.time || scheduled.scheduledTime,
  channel: reminder.channel || '',
  score: reminder.score || 0,
  reason: reminder.reason || '',
  status: 'scheduled',
  created_at: new Date().toISOString()
};

return [{json: {...scheduled, reminder_record: reminderRecord}}];

// Aggregate all reminder scheduling results
const allInputs = $input.all();
const base = $items('Process Request')[0]?.json || {};

const scheduled = allInputs.filter(i => i.json.success !== false);
const failed = allInputs.filter(i => i.json.success === false || i.json.error);

return [{
  json: {
    user_id: base.user_id,
    reminders_scheduled: scheduled.length,
    reminders_failed: failed.length,
    total_recommendations: base.reminder_count || 0,
    scheduled_reminders: scheduled.map(s => ({
      jobId: s.json.jobId,
      scheduledTime: s.json.scheduledTime || s.json.scheduled_time,
      channel: s.json.channel,
      score: s.json.score
    })),
    timestamp: new Date().toISOString()
  }
}];
